import React from "react";
import { useState } from "react";

export default function NoteCard({ note, onDelete, onEdit }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editedTitle, setEditedTitle] = useState(note.title || "");
  const [editedContent, setEditedContent] = useState(note.content || "");

  const handleEdit = () => setIsEditing(true);

  const handleSave = () => {
    if (onEdit) {
      onEdit({ ...note, title: editedTitle, content: editedContent });
    }
    setIsEditing(false);
  };

  const handleCancel = () => {
    setEditedTitle(note.title || "");
    setEditedContent(note.content || "");
    setIsEditing(false);
  };

  return (
    <div className="bg-white p-4 rounded shadow">
      {isEditing ? (
        <>
          <input
            className="font-semibold mb-2 w-full border rounded px-2 py-1"
            value={editedTitle}
            onChange={(e) => setEditedTitle(e.target.value)}
            placeholder="Title"
          />
          <textarea
            className="text-sm mb-4 w-full border rounded px-2 py-1"
            value={editedContent}
            onChange={(e) => setEditedContent(e.target.value)}
            placeholder="Content"
            rows={3}
          />
          <div className="flex justify-end gap-2">
            <button
              onClick={handleSave}
              className="bg-blue-500 text-white px-3 py-1 rounded text-sm"
            >
              Save
            </button>
            <button
              onClick={handleCancel}
              className="bg-gray-300 px-3 py-1 rounded text-sm"
            >
              Cancel
            </button>
          </div>
        </>
      ) : (
        <>
          {note.title && <h3 className="font-semibold mb-2">{note.title}</h3>}
          <p className="text-sm mb-4">{note.content}</p>
          <div className="flex justify-between items-center">
            <div className="text-xs text-gray-400">
              {new Date(note.createdAt).toLocaleString()}
            </div>
            <div className="flex gap-2">
              <button onClick={onDelete} className="text-red-500 text-sm">
                Delete
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
}
