import React from "react";
import { useState } from "react";

export default function NoteCard({ note, onDelete, onEdit }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(note.title || "");
  const [editContent, setEditContent] = useState(note.content || "");

  const handleSave = () => {
    if (onEdit) {
      onEdit({ ...note, title: editTitle, content: editContent });
    }
    setIsEditing(false);
  };

  return (
    <div className="bg-white p-4 rounded shadow">
      {isEditing ? (
        <div>
          <input
            className="font-semibold mb-2 w-full border rounded px-2 py-1"
            value={editTitle}
            onChange={(e) => setEditTitle(e.target.value)}
            placeholder="Title"
          />
          <textarea
            className="text-sm mb-4 w-full border rounded px-2 py-1"
            value={editContent}
            onChange={(e) => setEditContent(e.target.value)}
            placeholder="Content"
          />
          <div className="flex justify-end gap-2">
            <button
              onClick={handleSave}
              className="bg-blue-500 text-white px-3 py-1 rounded text-sm"
            >
              Save
            </button>
            <button
              onClick={() => setIsEditing(false)}
              className="bg-gray-300 px-3 py-1 rounded text-sm"
            >
              Cancel
            </button>
          </div>
        </div>
      ) : (
        <>
          {note.title && <h3 className="font-semibold mb-2">{note.title}</h3>}
          <p className="text-sm mb-4">{note.content}</p>
          <div className="flex justify-between items-center">
            <div className="text-xs text-gray-400">
              {new Date(note.createdAt).toLocaleString()}
            </div>
            <div className="flex gap-2">
              <button onClick={onDelete} className="text-red-500 text-sm">
                Delete
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
}
