import React, { useState } from "react";
import { sendOtp, verifyOtp } from "../api";

export default function AuthPage({ onLogin }) {
  const [email, setEmail] = useState("");
  const [phase, setPhase] = useState("enter-email"); // enter-email / enter-otp
  const [message, setMessage] = useState("");
  const [otp, setOtp] = useState("");

  const handleSendOtp = async () => {
    if (!email) {
      setMessage("Enter a valid email");
      return;
    }
    try {
      await sendOtp(email);
      setPhase("enter-otp");
      setMessage("OTP sent to your email (check spam).");
    } catch (err) {
      setMessage(err?.response?.data?.msg || "Failed to send OTP");
    }
  };

  const handleVerify = async () => {
    try {
      const res = await verifyOtp(email, otp);
      const { token } = res.data;
      onLogin(token);
    } catch (err) {
      setMessage(err?.response?.data?.msg || "Invalid OTP");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 p-4">
      <div className="bg-white rounded-2xl shadow-lg w-full max-w-4xl flex flex-col md:flex-row overflow-hidden">
        {/* Left Section: Form */}
        <div className="w-full md:w-1/2 p-8 flex flex-col justify-center">
          {/* Logo + Title */}
          <div className="flex items-center mb-6">
            <div className="w-6 h-6 bg-blue-500 rounded-full flex items-center justify-center text-white font-bold text-xs mr-2">
              HD
            </div>
            <span className="text-lg font-semibold">HD</span>
          </div>

          <h2 className="text-3xl font-bold mb-2">Sign up</h2>
          <p className="text-gray-500 mb-6 text-sm">
            Sign up to enjoy the feature of HD
          </p>

          {/* Name */}
          <input
            className="w-full border border-gray-300 p-3 rounded-lg mb-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />

          {/* DOB */}
          {/* <input
            type="date"
            className="w-full border border-gray-300 p-3 rounded-lg mb-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={dob}
            onChange={(e) => setDob(e.target.value)}
          /> */}

          {/* Email */}
          <input
            className="w-full border border-gray-300 p-3 rounded-lg mb-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          {/* OTP Button */}
          <button
            className="w-full bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 transition"
            onClick={handleSendOtp}
          >
            Get OTP
          </button>

          {/* Already have account */}
          <p className="mt-4 text-sm text-gray-600 text-center">
            Already have an account?{" "}
            <a
              href="/login"
              className="text-blue-600 font-medium hover:underline"
            >
              Sign in
            </a>
          </p>
        </div>

        {/* Right Section: Image */}
        <div className="hidden md:flex md:w-1/2 items-center justify-center bg-black">
          <img
            src="https://wallpapers.com/images/hd/windows-11-blue-abstract-flow-lsvr9jz9q4q2i1jh.jpg"
            alt="Illustration"
            className="object-cover h-full w-full"
          />
        </div>
      </div>
    </div>
  );
}
