import React from "react";
import { useState } from "react";

export default function NoteCard({ note, onDelete, onEdit }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(note.title || "");
  const [editContent, setEditContent] = useState(note.content || "");
  const [hovered, setHovered] = useState(false);

  const handleSave = () => {
    if (onEdit) {
      onEdit({ ...note, title: editTitle, content: editContent });
    }
    setIsEditing(false);
  };

  return (
    <div
      className={`bg-gradient-to-br from-blue-50 to-white p-5 rounded-xl shadow-lg transition-all duration-200 border border-gray-200 relative group`}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      {isEditing ? (
        <div>
          <input
            className="font-semibold mb-2 w-full border-2 border-blue-300 rounded px-2 py-1 focus:outline-none focus:border-blue-500 transition"
            value={editTitle}
            onChange={(e) => setEditTitle(e.target.value)}
            placeholder="Title"
            autoFocus
          />
          <textarea
            className="text-sm mb-4 w-full border-2 border-blue-300 rounded px-2 py-1 focus:outline-none focus:border-blue-500 transition resize-none"
            value={editContent}
            onChange={(e) => setEditContent(e.target.value)}
            placeholder="Content"
            rows={4}
          />
          <div className="flex justify-end gap-2">
            <button
              onClick={handleSave}
              className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-1 rounded text-sm font-medium shadow"
            >
              Save
            </button>
            <button
              onClick={() => setIsEditing(false)}
              className="bg-gray-300 hover:bg-gray-400 px-4 py-1 rounded text-sm font-medium"
            >
              Cancel
            </button>
          </div>
        </div>
      ) : (
        <>
          {note.title && (
            <h3 className="font-semibold mb-2 text-lg text-blue-700">
              {note.title}
            </h3>
          )}
          <p className="text-sm mb-4 text-gray-700 whitespace-pre-line">
            {note.content}
          </p>
          <div className="flex justify-between items-center mt-2">
            <div className="text-xs text-gray-400">
              {new Date(note.createdAt).toLocaleString()}
            </div>
            <div className="flex gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
              <button
                onClick={() => setIsEditing(true)}
                className="bg-blue-100 hover:bg-blue-200 text-blue-700 px-3 py-1 rounded text-sm font-medium shadow"
                title="Edit"
              >
                Edit
              </button>
              <button
                onClick={onDelete}
                className="bg-red-100 hover:bg-red-200 text-red-600 px-3 py-1 rounded text-sm font-medium shadow"
                title="Delete"
              >
                Delete
              </button>
            </div>
          </div>
        </>
      )}
      {hovered && !isEditing && (
        <span className="absolute top-2 right-3 text-xs text-gray-400 bg-white px-2 py-0.5 rounded shadow">
          Actions
        </span>
      )}
    </div>
  );
}
